import React, { useEffect, useState } from 'react'
import Nav from '../components/Nav'
import Footer from '../components/Footer'
import Card from '../components/Card'
import Carousel from '../components/Carousel'

export default function Home() {

  const [foodCat, setFoodCat] = useState([]);
  const [foodItem, setFoodItem] = useState([]);

  const loadData = async () => {
    let response = await fetch("http://localhost:5000/api/foodData", {
      method: "POST",
      headers: {
        'Content-Type': 'application/json'
      }
    });
    response = await response.json();
    // console.log(response[0], response[1]);
    setFoodCat(response[1]);
    setFoodItem(response[0]);
  }

  useEffect(() => {
    loadData();
  }, [])

  return (
    <div>
      <div><Nav /></div>
      <div><Carousel /></div>
      <div className='container'>
        {
          foodCat != []
            ? foodCat.map((data) => {
              return (
                <div className='row mb-3'>
                  <div key={data._id} className='fs-3 m-3 text-white'>{data.CategoryName}</div>
                  <hr />
                  {foodItem != []
                    ?
                    foodItem.filter((item)=> item.CategoryName === data.CategoryName).map(filterItems=>{
                      return(
                        <div className='col-12 col-md-6 col-lg-3' key={filterItems._id}>
                          <Card foodName={filterItems}></Card>
                        </div>
                      )
                    }) 
                    :<div>No data found</div>}
                </div>
              )
            })
            : <div>No data found</div>
        }
        
      </div>
      <div><Footer /> </div>
    </div>
  )
}
